$input = ""

$color = 0
$line = 0
$output = ""
foreach($i in $input.toCharArray()) {
    $output += $i
    
    $color += 1
    if($color -eq 6) {
        $output += " ";
        $color = 0
        $line += 1
    }
    if($line -eq 16) {
        $output += "`n"
        $line = 0
    }
}

$output | clip